mapper.insert:
insert into tblclass (classId,
className, createDate
)
values (#{classId,jdbcType=VARCHAR},
#{className,jdbcType=VARCHAR},
#{createDate,jdbcType=DATE}
)

mapper.update:
update tblclass
set className = #{className,jdbcType=VARCHAR},
createDate =
#{createDate,jdbcType=DATE}
where classId = #{classId,jdbcType=VARCHAR}

mapper.select:
SELECT DISTINCT c.*, i.instituteName ,stuNum.count as stuCount FROM tblclass c LEFT JOIN classInstitute ci ON ci.classId = c.classid LEFT JOIN institute i ON i.instituteId = ci.instituteId LEFT JOIN ( SELECT count(*) as count, classId FROM studentclass GROUP BY classId ) AS stuNum ON stuNum.classId = c.classId where 1=1
and i.instituteId = #{instituteId,jdbcType=VARCHAR}
and c.className = #{className,jdbcType=VARCHAR}
order by c.createDate desc LIMIT #{rowFrom,jdbcType=INTEGER} , #{limit,jdbcType=INTEGER}

SELECT count(*) FROM tblclass c LEFT JOIN classInstitute ci ON ci.classId = c.classid LEFT JOIN institute i ON i.instituteId = ci.instituteId where 1=1
and i.instituteId = #{instituteId,jdbcType=VARCHAR}
and c.className = #{className,jdbcType=VARCHAR}
